CREATE TABLE `sports_injurys` (
  `sportsinjury_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `sportsinjury_id` int(3) NOT NULL,
  `sportsinjury_symptoms` varchar(255) CHARACTER SET utf32 COLLATE utf32_unicode_ci NOT NULL,
  `sportsinjury_parts` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `sportsinjury_rehab` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `sportsinjurys_precautions` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- 傾印資料表的資料 `sports_injurys`
